public Employee getEmployeeProfile(WebSession s, String userId, String subjectUserId) throws UnauthorizedException {
    Employee profile = null;
    try {
        String query = "SELECT employee.* " + "FROM employee,ownership WHERE employee.userid = ownership.employee_id and " + "ownership.employer_id = " + userId + " and ownership.employee_id = " + subjectUserId;
        try {
            Statement answer_statement = WebSession.getConnection(s).createStatement(ResultSet.TYPE_SCROLL_INSENSITIVE, ResultSet.CONCUR_READ_ONLY);
            ResultSet answer_results = answer_statement.executeQuery(query);
            if (answer_results.next()) {
                profile = new Employee(answer_results.getInt("userid"), answer_results.getString("first_name"), answer_results.getString("last_name"), answer_results.getString("ssn"), answer_results.getString("title"), answer_results.getString("phone"), answer_results.getString("address1"), answer_results.getString("address2"), answer_results.getInt("manager"), answer_results.getString("start_date"), answer_results.getInt("salary"), answer_results.getString("ccn"), answer_results.getInt("ccn_limit"), answer_results.getString("disciplined_date"), answer_results.getString("disciplined_notes"), answer_results.getString("personal_description"));
            }
        } catch (SQLException sqle) {
            s.setMessage("Error getting employee profile");
            sqle.printStackTrace();
        }
    } catch (Exception e) {
        s.setMessage("Error getting employee profile");
        e.printStackTrace();
    }
    return profile;
}

