public void handleRequest(WebSession s) {
    try {
        if (s.getParser().getRawParameter("from", "").equals("ajax")) {
            if (s.getParser().getRawParameter(ACCOUNTID, "").equals("836239")) {
                String lineSep = System.getProperty("line.separator");
                String xmlStr = "<root>" + lineSep + "<reward>WebGoat Mug 20 Pts</reward>" + lineSep + "<reward>WebGoat t-shirt 50 Pts</reward>" + lineSep + "<reward>WebGoat Secure Kettle 30 Pts</reward>" + lineSep + "</root>";
                s.getResponse().setContentType("text/xml");
                s.getResponse().setHeader("Cache-Control", "no-cache");
                PrintWriter out = new PrintWriter(s.getResponse().getOutputStream());
                out.print(xmlStr);
                out.flush();
                out.close();
                return;
            }
        }
    } catch (Exception ex) {
        ex.printStackTrace();
    }
    Form form = new Form(getFormAction(), Form.POST).setName("form").setEncType("");
    form.addElement(createContent(s));
    setContent(form);
}

protected Element createContent(WebSession s) {
    ElementContainer ec = new ElementContainer();
    boolean isDone = false;
    init();
    if (s.getParser().getRawParameter("done", "").equals("yes")) {
        isDone = true;
    }
    String lineSep = System.getProperty("line.separator");
    String script = "<script>" + lineSep + "function getRewards() {" + lineSep + "var accountIDField = document.getElementById(\'" + ACCOUNTID + "\');" + lineSep + "if (accountIDField.value.length < 6 ) { return; }" + lineSep + "var url = \'" + getLink() + "&from=ajax&" + ACCOUNTID + "=\' + encodeURIComponent(accountIDField.value);" + lineSep + "if (typeof XMLHttpRequest != \'undefined\') {" + lineSep + "req = new XMLHttpRequest();" + lineSep + "} else if (window.ActiveXObject) {" + lineSep + "req = new ActiveXObject(\'Microsoft.XMLHTTP\');" + lineSep + "   }" + lineSep + "   req.open(\'GET\', url, true);" + lineSep + "   req.onreadystatechange = callback;" + lineSep + "   req.send(null);" + lineSep + "}" + lineSep + "function callback() {" + lineSep + "    if (req.readyState == 4) { " + lineSep + "        if (req.status == 200) { " + lineSep + "            var rewards = req.responseXML.getElementsByTagName(\'reward\');" + lineSep + "\t\t\t var rewardsDiv = document.getElementById(\'rewardsDiv\');" + lineSep + "\t\t\t\trewardsDiv.innerHTML = \'\';" + lineSep + "\t\t\t\tvar strHTML=\'\';" + lineSep + "\t\t\t\tstrHTML = \'<tr><td>&nbsp;</td><td><b>Rewards</b></td></tr>\';" + lineSep + "\t\t\t for(var i=0; i< rewards.length; i++){" + lineSep + "\t\t\t\tstrHTML = strHTML + \'<tr><td><input name=\"check\' + (i+1001) +\'\" type=\"checkbox\"></td><td>\';" + lineSep + "\t\t\t    strHTML = strHTML + rewards[i].firstChild.nodeValue + \'</td></tr>\';" + lineSep + "\t\t\t }" + lineSep + "\t\t\t\tstrHTML = \'<table>\' + strHTML + \'</table>\';" + lineSep + "\t\t\t\tstrHTML = \'Your account balance is now 100 points<br><br>\' + strHTML;" + lineSep + "               rewardsDiv.innerHTML = strHTML;" + lineSep + "        }}}" + lineSep + "</script>" + lineSep;
    if (!isDone) {
        ec.addElement(new StringElement(script));
    }
    ec.addElement(new BR().addElement(new H1().addElement("Welcome to WebGoat-Miles Reward Miles Program.")));
    ec.addElement(new BR());
    ec.addElement(new BR().addElement(new H3().addElement("Rewards available through the program:")));
    ec.addElement(new BR());
    Table t2 = new Table().setCellSpacing(0).setCellPadding(0).setBorder(0).setWidth("90%").setAlign("center");
    TR trRewards = null;
    for (int i = 1001; i < 1001 + rewardsMap.size(); i++) {
        trRewards = new TR();
        Reward r = (Reward)rewardsMap.get(i);
        trRewards.addElement(new TD("-" + r.getName()));
        trRewards.addElement(new TD(r.getPoints() + " Pts"));
        t2.addElement(trRewards);
    }
    ec.addElement(t2);
    ec.addElement(new BR());
    ec.addElement(new H3().addElement("Redeem your points:"));
    ec.addElement(new BR());
    Table t1 = new Table().setCellSpacing(0).setCellPadding(0).setBorder(0).setWidth("90%").setAlign("center");
    TR tr = new TR();
    tr.addElement(new TD("Please enter your account ID:"));
    Input input1 = new Input(Input.TEXT, ACCOUNTID, "");
    input1.addAttribute("onkeyup", "getRewards();");
    input1.addAttribute("id", ACCOUNTID);
    tr.addElement(new TD(input1));
    t1.addElement(tr);
    ec.addElement(t1);
    ec.addElement(new BR());
    ec.addElement(new BR());
    ec.addElement(new BR());
    Div div = new Div();
    div.addAttribute("name", "rewardsDiv");
    div.addAttribute("id", "rewardsDiv");
    ec.addElement(div);
    Input b = new Input();
    b.setType(Input.SUBMIT);
    b.setValue("Submit");
    b.setName("SUBMIT");
    ec.addElement(b);
    if (s.getParser().getRawParameter("SUBMIT", "") != "") {
        if (s.getParser().getRawParameter("check1004", "") != "") {
            makeSuccess(s);
        } else {
            StringBuffer shipment = new StringBuffer();
            for (int i = 1001; i < 1001 + rewardsMap.size(); i++) {
                if (s.getParser().getRawParameter("check" + i, "") != "") {
                    shipment.append(((Reward)rewardsMap.get(i)).getName() + "<br>");
                }
            }
            shipment.insert(0, "<br><br><b>The following items will be shipped to your address:</b><br>");
            ec.addElement(new StringElement(shipment.toString()));
        }
    }
    return ec;
}

