public String getIPParameter(String name) throws ParameterNotFoundException {
    boolean valid = true;
    String[] values = request.getParameterValues(name);
    String value;
    if (values == null) {
        throw new ParameterNotFoundException(name + " not found");
    } else if (values[0].length() == 0) {
        throw new ParameterNotFoundException(name + " was empty");
    } else {
        value = clean(values[0].trim());
        if (value.indexOf("&") > 0) {
            value = value.substring(0, value.indexOf("&"));
        }
        int startIndex = 0;
        int endIndex = 0;
        int octetCount = 0;
        int octetValue;
        String octet;
        if (value.indexOf(".") >= 0) {
            while ((valid == true) && (octetCount < 4)) {
                endIndex = value.indexOf(".", startIndex);
                if (endIndex == -1) {
                    endIndex = value.length();
                }
                octet = value.substring(startIndex, endIndex);
                startIndex = endIndex + 1;
                try {
                    octetValue = Integer.parseInt(octet);
                    if ((octetValue <= 0) || (octetValue >= 256)) {
                        valid = false;
                    }
                } catch (Exception e) {
                    valid = false;
                }
                octetCount++;
            }
        } else {
            valid = false;
        }
        if (value.length() != endIndex) {
            valid = false;
        }
        return valid ? value : null;
    }
}

