public String toString() {
    StringBuffer s = new StringBuffer("[");
    Enumeration e = getParameterNames();
    while (e.hasMoreElements()) {
        String key = (String)e.nextElement();
        s.append(key + "=" + getParameterValues(key)[0]);
        if (e.hasMoreElements()) {
            s.append(",");
        }
    }
    s.append("]");
    return (s.toString());
}

