public static String encode(String s1) {
    StringBuffer buf = new StringBuffer();
    int i;
    for (i = 0; i < s1.length(); ++i) {
        char ch = s1.charAt(i);
        String entity = i2e.get(new Integer((int)ch));
        if (entity == null) {
            if (((int)ch) > 128) {
                buf.append("&#" + ((int)ch) + ";");
            } else {
                buf.append(ch);
            }
        } else {
            buf.append("&" + entity + ";");
        }
    }
    return buf.toString();
}

public static String decode(String s1) {
    StringBuffer buf = new StringBuffer();
    int i;
    for (i = 0; i < s1.length(); ++i) {
        char ch = s1.charAt(i);
        if (ch == '&') {
            int semi = s1.indexOf(';', i + 1);
            if (semi == -1) {
                buf.append(ch);
                continue;
            }
            String entity = s1.substring(i + 1, semi);
            Integer iso;
            if (entity.charAt(0) == '#') {
                iso = new Integer(entity.substring(1));
            } else {
                iso = e2i.get(entity);
            }
            if (iso == null) {
                buf.append("&" + entity + ";");
            } else {
                buf.append((char)(iso.intValue()));
            }
            i = semi;
        } else {
            buf.append(ch);
        }
    }
    return buf.toString();
}
